{{groovy jars="attach:ReaderCode.OPMLImportSheet@opml-0.3-SNAPSHOT.jar"}}
import com.sun.syndication.io.WireFeedInput;
import com.sun.syndication.feed.opml.Opml;
import com.sun.syndication.feed.opml.Outline;
import com.sun.syndication.io.impl.OPML10Parser;
import com.sun.syndication.io.impl.FeedParsers;
import java.io.InputStreamReader;
import java.io.ByteArrayInputStream;
import java.io.StringReader;
import java.net.URL

/* Mainly targeting google reader subscription TBH. 
   Would need to check if other readers export using the same strategy
   of nesting subscriptions with categories / tags in an outline.  */

def currentTag = ''
def existingSourceUrls = []

def escapeLtGt = {
  return it.replaceAll('<', '&lt;').replaceAll('>', '&gt;')
}

def outputOutline = { outline, isChild ->
  if (!outline.xmlUrl || outline.xmlUrl == '') {
    currentTag = outline.text
  } else if (!isChild) {
    currentTag = ''
  }
  if (outline.xmlUrl != '' && outline.xmlUrl) {
    def wouldBeDoc = xwiki.getDocument(xwiki.getUniquePageName(doc.space, outline.title))
    if(!existingSourceUrls.contains(outline.xmlUrl.trim())) {
      println """
      <li class="xitem importItem">
        <div class="xitemcontainer">
          <input type="checkbox" name="selected" value="${wouldBeDoc.name}" checked="checked" />
          <input type="text" name="url_${wouldBeDoc.name}" value="${outline.xmlUrl}" />
          <input type="text" name="title_${wouldBeDoc.name}" value="${escapeLtGt(outline.title)}" />
      """
      if (currentTag != '') {
         // TODO display tag edit UI when aggregators support tags in the reader.
         // println """<span class="tag">${currentTag}</span>"""
      }
      println """
        </div>
      </li>
      """
    } else {
      println"""
      <li class="xitem importItem alreadyExists">
        <div class="xitemcontainer">
          <div>
            <span class="warningmessage box">${msg.get('reader.opmlImport.sourceAlreadyExists')}</span>
          </div>
          <div>
            <input type="checkbox" name="selected" value="${wouldBeDoc.name}" disabled="disabled" />
            <input type="text" name="url_${wouldBeDoc.name}" value="${outline.xmlUrl}" disabled="disabled" />
            <input type="text" name="title_${wouldBeDoc.name}" value="${escapeLtGt(outline.title)}" disabled="disabled" />
          </div>
        </div>
      </li>
      """
    }
  }
}

if (request.xaction && request.xaction == 'importOpml') {

  if (request.getParameterValues('selected').size() > 0) {

    println """**${msg.get('reader.opmlImport.listOfImportedSources')}**"""

    request.getParameterValues('selected').each {
      def title = request.getParameter("title_${it}")
      def url = request.getParameter("url_${it}")
      def document = xwiki.getDocument(xwiki.getUniquePageName(doc.space, title))
      def source = document.getObject('XWiki.AggregatorURLClass', true)
      // Title, parent, content 
      document.setTitle(title)
      document.setParent("${doc.space}.Sources")
      document.setSyntaxId('xwiki/2.1')
      document.setContent("{{include document='ReaderCode.FeedSheet' /}}")
      source.set('title', title)
      // Duplicate name in source object
      source.set('name', document.name)
      source.set('url', url)
      document.save(msg.get('reader.versionSummary.sourceCreatedFromOpml'))
      println """* [[{{{${title}}}}>>${document.name}]]"""
    }
  }
  else {
    println """{{warning}}${msg.get('reader.opmlImport.nothingToImport')}{{/warning}}"""
  }

  println """[[${msg.get('reader.opmlImport.backToTheImportWizard')}>>${doc.fullName}]]"""

}
else if (request.import_file && request.import_file != '') {

  /* ROME does not let us add parsers at run time, but whatever, WE WILL DO IT ANYWAY ! */
  FeedParsers.metaClass.getParserFor = { ->
    return new OPML10Parser();
  }

  existingSourceUrls = xwiki.search("select distinct source.url from BaseObject as obj, XWiki.AggregatorURLClass as source where obj.className='XWiki.AggregatorURLClass' and obj.id=source.id")

  println """

{{html wiki=false}}"""

  try {
    WireFeedInput input = new WireFeedInput();
    Opml feed = (Opml)input.build( new StringReader(doc.getAttachment(request.import_file).contentAsString) );
    List<Outline> outlines = (List<Outline>) feed.getOutlines();
    println """
      <form action="" class="xform selectSources" method="post">
        <div>
          <input type="hidden" name="xaction" value="importOpml" />
          <input type="hidden" name="import_file" value="${request.import_file}" />
          <ul class="xlist importList">
    """
    outlines.each { 
       outputOutline(it, false)
       it.children.each { child -> outputOutline(child, true) } 
    }
    println """
          </ul>
        </div>
        <div class="buttonwrapper">
          <input type="submit" class="button" value="${msg.get('reader.opmlImport.doImport')}">
        </div>
      </form>
{{/html}}

    """
  } catch(Exception e) {
e.printStackTrace();
    println """{{/html}}

{{error}}${msg.get('reader.opmImport.failedToLoadFile', [e.message])}{{/error}}"""
  }
}


{{/groovy}}

